import React, { useEffect, useState } from 'react';
import './Profile.css';
import { useNavigate } from 'react-router-dom';

export default function Profile() {
  const [email, setEmail] = useState('');
  const [paidYear, setPaidYear] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('currentUser'));
    if (!user) {
      alert('Please login first.');
      navigate('/login');
    } else {
      setEmail(user.email);
      setPaidYear(user.paidYear || '');
      setPassword(user.password || '');
    }
  }, [navigate]);

  const handleSave = () => {
    const allUsers = JSON.parse(localStorage.getItem('users')) || [];
    const updatedUsers = allUsers.map(user => {
      if (user.email === email) {
        return { ...user, paidYear, password };
      }
      return user;
    });
    localStorage.setItem('users', JSON.stringify(updatedUsers));
    localStorage.setItem('currentUser', JSON.stringify({ email, paidYear, password }));
    alert('Profile updated successfully.');
  };

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    navigate('/login');
  };

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      <label>Email (read-only)</label>
      <input value={email} disabled />

      <label>Paid Year</label>
      <input
        type="number"
        value={paidYear}
        onChange={(e) => setPaidYear(e.target.value)}
      />

      <label>Password</label>
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <button onClick={handleSave}>Save Changes</button>
      <button className="logout-btn" onClick={handleLogout}>Logout</button>
    </div>
  );
}
